sysauto_script.script:
//To get time right now
var rightNow = new GlideDateTime();

//Selecting the table to get records
var overDueNeedItTasks = new GlideRecord("x_58872_needit_needit_task");
//Query 
overDueNeedItTasks.addQuery("due_date","<=",rightNow);
overDueNeedItTasks.addQuery("state","!=",3);

//Execute Query
overDueNeedItTasks.query();

while(overDueNeedItTasks.next())
	{
		gs.info("Overdue NeedIt Task records: " + overDueNeedItTasks.number);
		overDueNeedItTasks.priority = 2;
		overDueNeedItTasks.update();	//gs.eventQueue('x_58872_needit.OverdueNeedItTask',overDueNeedItTasks,overDueNeedItTasks.number,gs.getUserName());
	}
